package 연습용프로젝트1;

public class N10산술연산자2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 90;//인트변수 a는 90
		int b = 70;//인트변수 b는 70
		
		int 총합 = a+b;//총점을 산술연산자이용해 구해라
		System.out.println(총합);
		double 평균 = 총합/2.0;//형균을 구하는데 실수값으로 구해라 
		System.out.println(평균);
	}

}
