package 연습용프로젝트1;

public class N11증가감소연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int 겜점수 =150;

int 겜점수2=++겜점수;//먼저겜점수가 1 증가후 겜점수2에 대입
System.out.println(겜점수2);
int 겜점수3 =--겜점수;//먼저겜점수가 1감소후 겜점수3에 대입
System.out.println(겜점수3);
int 겜점수4=겜점수++;//겜점수4에 기존겜점수값을 먼저 대입한후 겜점수 1증가
System.out.println(겜점수4);
int 겜점수5=겜점수--;//겜점수5에 기존겜점수값을 먼저 대입한후 겜점수 1감소
System.out.println(겜점수5);}///평행을 맞추려고 한다고 생각하자

}
